import React, { useState, useEffect } from "react";
import Web3 from 'web3';
import Healthcare from '../src/contractscopy/Healthcare.json';

function App() {
  const [patientAddress, setPatientAddress] = useState('');
  const [medicalRecords, setMedicalRecords] = useState([]);
  const [web3, setWeb3] = useState(null);
  const [healthcareContract, setHealthcareContract] = useState(null);

  // Initialize web3 and Healthcare contract
  const initWeb3 = async () => {
    if (window.ethereum) {
      const web3Instance = new Web3(window.ethereum);
      try {
        await window.ethereum.enable();
        setWeb3(web3Instance);
        const networkId = await web3Instance.eth.net.getId();
        const deployedNetwork = Healthcare.networks[networkId];
        const healthcareInstance = new web3Instance.eth.Contract(
          Healthcare.abi,
          deployedNetwork && deployedNetwork.address,
        );
        setHealthcareContract(healthcareInstance);
      } catch (error) {
        console.error(error);
      }
    }
  };

  // Get medical records of the patient
  const getMedicalRecords = async () => {
    if (patientAddress && healthcareContract) {
      try {
        const records = await healthcareContract.methods.getMedicalRecords(patientAddress).call();
        setMedicalRecords(records);
      } catch (error) {
        console.error(error);
      }
    }
  };

  // Handle input change
  const handleInputChange = (event) => {
    setPatientAddress(event.target.value);
  };

  // Handle form submit
  const handleSubmit = (event) => {
    event.preventDefault();
    getMedicalRecords();
  };

  // Initialize web3 and Healthcare contract on component mount
  useEffect(() => {
    initWeb3();
  }, []);

  return (
    <div>
      <h1>Healthcare Application</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Patient Address:
          <input type="text" value={patientAddress} onChange={handleInputChange} />
        </label>
        <button type="submit">Get Medical Records</button>
      </form>
      {medicalRecords.length > 0 && (
        <div>
          <h2>Medical Records</h2>
          <ul>
            {medicalRecords.map((record, index) => (
              <li key={index}>{record}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
