import React, { useState, useEffect } from 'react';
import Web3 from 'web3';
import Calend3 from '../src/contractscopy/';

function App() {
  const [web3, setWeb3] = useState(undefined);
  const [accounts, setAccounts] = useState([]);
  const [contract, setContract] = useState(undefined);
  const [appointments, setAppointments] = useState([]);
  const [title, setTitle] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');

  useEffect(() => {
    const init = async () => {
      try {
        // Check if Web3 is injected by the browser
        if (typeof window.ethereum !== 'undefined') {
          const web3Instance = new Web3(window.ethereum);
          await window.ethereum.enable();
          setWeb3(web3Instance);
        }
      } catch (error) {
        console.error(error);
      }
    };
    init();
  }, []);

  useEffect(() => {
    const loadAccounts = async () => {
      const accounts = await web3.eth.getAccounts();
      setAccounts(accounts);
    };
    if (web3) {
      loadAccounts();
    }
  }, [web3]);

  useEffect(() => {
    const loadContract = async () => {
      const networkId = await web3.eth.net.getId();
      const networkData = Calend3.networks[networkId];
      if (networkData) {
        const contract = new web3.eth.Contract(Calend3.abi, networkData.address);
        setContract(contract);
      } else {
        window.alert('Contract not deployed to detected network.');
      }
    };
    if (web3) {
      loadContract();
    }
  }, [web3]);

  useEffect(() => {
    const loadAppointments = async () => {
      if (contract) {
        const appointmentCount = await contract.methods.appointmentsCount().call();
        const appointments = [];
        for (let i = 0; i < appointmentCount; i++) {
          const appointment = await contract.methods.appointments(i).call();
          appointments.push(appointment);
        }
        setAppointments(appointments);
      }
    };
    if (contract) {
      loadAppointments();
    }
  }, [contract]);

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleStartTimeChange = (event) => {
    setStartTime(event.target.value);
  };

  const handleEndTimeChange = (event) => {
    setEndTime(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const amountPaid = ((endTime - startTime) / 60) * (await contract.methods.getRate().call());
      await contract.methods.createAppointment(title, startTime, endTime).send({ from: accounts[0], value: amountPaid });
      window.alert('Appointment created successfully.');
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
  };

  if (!web3) {
    return <div>Loading Web3, accounts, and contract...</div>;
  }

  return (
    <div className="App">
      <h1>Calend3</h1>
      <h2>Appointments</h2>
      <ul>
        {appointments.map((appointment, index) => (
          <li key={index}>
            <h3>{appointment.title}</h3>
            <p>Start time: {new Date(parseInt(appointment.startTime) * 1000).toString()}</p>
            <p>End time:{new Date(parseInt(appointment.endTime) * 1000).toString()}</p>
<p>Paid: {web3.utils.fromWei(appointment.amountPaid)} ETH</p>
</li>
))}
</ul>
<h2>Create Appointment</h2>
<form onSubmit={handleSubmit}>
<label>
Title:
<input type="text" value={title} onChange={handleTitleChange} required />
</label>
<br />
<label>
Start Time:
<input type="datetime-local" value={startTime} onChange={handleStartTimeChange} required />
</label>
<br />
<label>
End Time:
<input type="datetime-local" value={endTime} onChange={handleEndTimeChange} required />
</label>
<br />
<button type="submit">Create Appointment</button>
</form>
</div>
);
}

export default App;
