import React, { useState } from "react";
import Web3 from "web3";
import AppointmentBooking from "./contracts/AppointmentBooking.json";

const web3 = new Web3(Web3.givenProvider);

function App() {
  const [appointmentBooking, setAppointmentBooking] = useState(null);
  const [account, setAccount] = useState(null);
  const [appointmentDate, setAppointmentDate] = useState("");
  const [appointments, setAppointments] = useState([]);

  const connectMetaMask = async () => {
    try {
      await window.ethereum.request({ method: "eth_requestAccounts" });
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = AppointmentBooking.networks[networkId];
      const instance = new web3.eth.Contract(
        AppointmentBooking.abi,
        deployedNetwork && deployedNetwork.address
      );
      setAppointmentBooking(instance);
      const accounts = await web3.eth.getAccounts();
      setAccount(accounts[0]);
    } catch (error) {
      console.error(error);
    }
  };

  const bookAppointment = async () => {
    const price = web3.utils.toWei(
      "1", // 1 ETH per appointment
      "ether"
    );
    const timestamp = new Date(appointmentDate).getTime() / 1000; // convert to Unix timestamp
    await appointmentBooking.methods.bookAppointment(timestamp).send({
      from: account,
      value: price,
    });
    alert("Appointment booked successfully");
  };

  const loadAvailableAppointments = async () => {
    const availableAppointments = await appointmentBooking.methods
      .getAvailableAppointments()
      .call();
    setAppointments(availableAppointments);
  };

  return (
    <div>
      {account ? (
        <div>
          <p>Account: {account}</p>
          <input
            type="datetime-local"
            value={appointmentDate}
            onChange={(e) => setAppointmentDate(e.target.value)}
          />
          <button onClick={bookAppointment}>Book Appointment</button>
          <button onClick={loadAvailableAppointments}>Load Available Appointments</button>
          <ul>
            {availableAppointments.map((appointment, index) => (
              <li key={index}>{new Date(appointment * 1000).toLocaleString()}</li>
            ))}
          </ul>
        </div>
      ) : (
        <button onClick={connectMetaMask}>Connect with MetaMask</button>
      )}
    </div>
  );
}

export default App;
