import React, { useState } from "react";
import Web3 from "web3";
import DoctorAppointment from "../src/contractscopy/MedicalRecord.json";

const web3 = new Web3(Web3.givenProvider);

function App() {
  const [doctorAppointment, setDoctorAppointment] = useState(null);
  const [account, setAccount] = useState(null);
  const [day, setDay] = useState("");
  const [appointmentTime, setAppointmentTime] = useState("");

  const connectMetaMask = async () => {
    try {
      await window.ethereum.request({ method: "eth_requestAccounts" });
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = DoctorAppointment.networks[networkId];
      const instance = new web3.eth.Contract(
        DoctorAppointment.abi,
        deployedNetwork && deployedNetwork.address
      );
      setDoctorAppointment(instance);
      const accounts = await web3.eth.getAccounts();
      setAccount(accounts[0]);
    } catch (error) {
      console.error(error);
    }
  };

  const bookAppointment = async () => {
    const appointmentSlot =
      appointmentTime / doctorAppointment.appointmentDuration;
    const isBooked = await doctorAppointment.methods
      .appointments(day.toString(), appointmentSlot.toString())
      .booked()
      .call();
    if (isBooked) {
      alert("Appointment already booked");
      return;
    }
    await doctorAppointment.methods
      .bookAppointment(day.toString(), appointmentTime.toString())
      .send({
        from: account,
        value:
          doctorAppointment &&
          doctorAppointment.appointmentFee &&
          doctorAppointment.appointmentFee.toString(),
      });
    alert("Appointment booked successfully");
  };

  const cancelAppointment = async () => {
    const appointmentSlot =
      appointmentTime / doctorAppointment.appointmentDuration;
    const appointment = await doctorAppointment.methods
      .appointments(day.toString(), appointmentSlot.toString())
      .call();
    if (!appointment.booked) {
      alert("No appointment found");
      return;
    }
    if (appointment.patient.toLowerCase() !== account.toLowerCase()) {
      alert("Only patient who booked the appointment can cancel it");
      return;
    }
    if (appointment.paid) {
      alert("Appointment fee already paid");
      return;
    }
    await doctorAppointment.methods
      .cancelAppointment(day.toString(), appointmentTime.toString())
      .send({ from: account });
    alert("Appointment canceled successfully");
  };

  return (
    <div>
      {account ? (
        <div>
          <p>Account: {account}</p>
          <p>Appointment duration: {doctorAppointment?.appointmentDuration}</p>
          <p>
            Appointment fee:{" "}
            {web3.utils.fromWei(
              doctorAppointment &&
                doctorAppointment.appointmentFee &&
                doctorAppointment.appointmentFee.toString()
            )}{" "}
            ETH
          </p>
          <p>
            Max appointments per day: {doctorAppointment?.maxAppointmentsPerDay}
          </p>
          <div>
            <label>Day: </label>
            <input
              type="number"
              value={day}
              onChange={(e) => setDay(e.target.value)}
            />
          </div>
          <div>
            <label>Appointment time: </label>
            <input
              type="number"
              value={appointmentTime}
              onChange={(e) => setAppointmentTime(e.target.value)}
            />
          </div>
          <button onClick={bookAppointment}>Book Appointment</button>
          <button onClick={cancelAppointment}>Cancel Appointment</button>
        </div>
      ) : (
        <button onClick={connectMetaMask}>Connect with MetaMask</button>
      )}
    </div>
  );
}

export default App;
